import React from "react";
import axios from "axios"
import ItemList from "./ItemList";
import ItemDetails from "./ItemDetails";
import ItemInfo from "./ItemInfo";

 class ProductDeatils extends React.Component {
    state={ image:[] };

    componentDidMount=async (term)=>{
      const respons=await  axios.get("https://api.unsplash.com/search/photos",{
            params:{query:"dresses"},
            headers:{
                Authorization: "Client-ID 514ce030305c360bdb652f274463bb5a5a34a9d4f8bfe4eb41dcb5cf86fc628b"
            }
        });
       this.setState({image:respons.data.results})
    }

    render(){
        console.log(this.state.image)
        return(
            <div>
                <ItemList images={this.state.image} />
                <ItemDetails currentimg={this.state.currentimg} />
                <ItemInfo details={this.state.details} />
            </div>
        )
        }
 }

 export default ProductDeatils;
