import React from "react";
 const ItemInfo=({info})=> {
    const renderInfo=()=>{
        return(
            <div className="my-5">
                <h1>{info.name}</h1>
                <div>
                    <p><span className="font-weight-bold  ">Price:$</span>{info.price}</p>
                    <p><span className="font-weight-bold ">DisCount :</span>{info.discount}<span className="text-bold text-danger">%off</span></p>
                </div>

                <div className="btn-group">
                <button className="btn btn-primary" type="button">Left</button>
                <button className="btn btn-danger" type="button">Middle</button>
                <button className="btn btn-warning" type="button">Right</button>
                </div>
            </div>
        )
    }

        return(
            <div className="col-md-4">{renderInfo()}</div>
        )
    
 }

 export default ItemInfo;



